# Script PowerShell per trovare l'IP locale
# Esegui: .\scripts\get-local-ip.ps1

Write-Host "`n🔍 Cercando IP locale..." -ForegroundColor Cyan
Write-Host ""

# Ottieni tutti gli IP
$ipAddresses = Get-NetIPAddress -AddressFamily IPv4 | Where-Object { $_.IPAddress -notlike "127.*" -and $_.IPAddress -notlike "169.254.*" }

if ($ipAddresses.Count -eq 0) {
    Write-Host "❌ Nessun IP trovato!" -ForegroundColor Red
    exit 1
}

Write-Host "✅ IP trovati:" -ForegroundColor Green
Write-Host ""

foreach ($ip in $ipAddresses) {
    $interface = Get-NetAdapter | Where-Object { $_.InterfaceIndex -eq $ip.InterfaceIndex }
    Write-Host "  📡 $($ip.IPAddress)" -ForegroundColor Yellow
    Write-Host "     Interfaccia: $($interface.Name)" -ForegroundColor Gray
    Write-Host "     Stato: $($interface.Status)" -ForegroundColor Gray
    Write-Host ""
}

# Seleziona il primo IP attivo (solitamente quello giusto)
